svg.script:
    const path2 = document.getElementById('path2');
let segundo = 0;

function changeFill() {
  if (path2.getAttribute('fill') === '#000') {
    path2.setAttribute('fill', '#fff');
    path2.style.filter = 'drop-shadow(10px 10px 100px #fff)';
  } else {
    path2.setAttribute('fill', '#000');
    path2.style.filter = 'drop-shadow(0px 0px 0px #fff)';
  }
}

path2.setAttribute('fill', '#000');

setInterval(() => {
  segundo++;

  if (segundo === 2 || segundo === 8) {
    changeFill();
  } else if (segundo === 10) {
    segundo = 0;
  }
}, 1000);
